##############################################################################

usage() {
	echo "Program to dynamically generate CRUD code for Transmilenio DB"
	echo "Requires cli-spring-boot-scaffold"
	echo -e "\nUsage:" 
	echo -e "\t$0 \t\t\t Generate code"
	echo -e "\t$0 -h\t\t\t Get help" 
	echo -e "\t$0 --help\t\t Get help" 
}


if [  $# -gt 0 ] 
then 
	if [[ ( $# == "--help") ||  $# == "-h" ]] 
	then 
		usage
		exit 0

	else
		usage
		exit 1
	fi
fi 

